INSERT INTO `t_plugin_copy1` VALUES (1, 'Git', 'git', 'BASE_ENV', '分布式版本控制系统', 'GitBranchSharp', '[\"2.40\", \"2.41\", \"latest\"]', 'latest', '#!/bin/bash\r\necho \"开始安装 Git ${VERSION}...\"\r\nif command -v git &> /dev/null; then\r\n    echo \"Git 已安装，版本: $(git --version)\"\r\n    exit 0\r\nfi\r\nyum install -y git || apt-get install -y git\r\necho \"Git 安装完成: $(git --version)\"', '#!/bin/bash\r\necho \"开始卸载 Git...\"\r\nyum remove -y git || apt-get remove -y git\r\necho \"Git 卸载完成\"', NULL, 1, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
INSERT INTO `t_plugin_copy1` VALUES (2, 'JDK 8', 'jdk8', 'BASE_ENV', 'Java Development Kit 8', 'CodeSharp', '[\"8u391\", \"8u392\", \"8u401\"]', '8u401', '#!/bin/bash\r\necho \"开始安装 JDK 8 (${VERSION})...\"\r\nmkdir -p ${INSTALL_PATH}\r\ncd /tmp\r\nwget https://download.oracle.com/otn/java/jdk/${VERSION}/jdk-${VERSION}-linux-x64.tar.gz\r\ntar -zxvf jdk-${VERSION}-linux-x64.tar.gz -C ${INSTALL_PATH}\r\necho \"export JAVA_HOME=${INSTALL_PATH}/jdk1.8.0_${VERSION#8u}\" >> ~/.bashrc\r\necho \"export PATH=$JAVA_HOME/bin:$PATH\" >> ~/.bashrc\r\nsource ~/.bashrc\r\necho \"JDK 8 安装完成\"', '#!/bin/bash\r\necho \"开始卸载 JDK 8...\"\r\nrm -rf ${INSTALL_PATH}\r\nsed -i \"/JAVA_HOME/d\" ~/.bashrc\r\necho \"JDK 8 卸载完成\"', NULL, 2, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
INSERT INTO `t_plugin_copy1` VALUES (3, 'JDK 17', 'jdk17', 'BASE_ENV', 'Java Development Kit 17 LTS', 'CodeSharp', '[\"17.0.9\", \"17.0.10\", \"17.0.11\"]', '17.0.11', '#!/bin/bash\r\necho \"开始安装 JDK 17 (${VERSION})...\"\r\nmkdir -p ${INSTALL_PATH}\r\ncd /tmp\r\nwget https://download.oracle.com/java/17/latest/jdk-17_linux-x64_bin.tar.gz\r\ntar -zxvf jdk-17_linux-x64_bin.tar.gz -C ${INSTALL_PATH}\r\necho \"export JAVA_HOME=${INSTALL_PATH}/jdk-17\" >> ~/.bashrc\r\necho \"export PATH=$JAVA_HOME/bin:$PATH\" >> ~/.bashrc\r\nsource ~/.bashrc\r\necho \"JDK 17 安装完成\"', '#!/bin/bash\r\necho \"开始卸载 JDK 17...\"\r\nrm -rf ${INSTALL_PATH}\r\nsed -i \"/JAVA_HOME/d\" ~/.bashrc\r\necho \"JDK 17 卸载完成\"', NULL, 3, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
INSERT INTO `t_plugin_copy1` VALUES (4, 'Node.js LTS', 'nodejs', 'BASE_ENV', 'JavaScript 运行时环境', 'LogoNodejs', '[\"18.19.0\", \"20.11.0\", \"latest\"]', '20.11.0', '#!/bin/bash\r\necho \"开始安装 Node.js ${VERSION}...\"\r\ncurl -fsSL https://rpm.nodesource.com/setup_20.x | bash -\r\nyum install -y nodejs || apt-get install -y nodejs\r\necho \"Node.js 安装完成: $(node --version)\"\r\necho \"NPM 版本: $(npm --version)\"', '#!/bin/bash\r\necho \"开始卸载 Node.js...\"\r\nyum remove -y nodejs || apt-get remove -y nodejs\r\necho \"Node.js 卸载完成\"', NULL, 4, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
INSERT INTO `t_plugin_copy1` VALUES (5, 'Docker', 'docker', 'BASE_ENV', '容器化平台', 'LogoDocker', '[\"24.0\", \"25.0\", \"latest\"]', 'latest', '#!/bin/bash\r\necho \"开始安装 Docker...\"\r\ncurl -fsSL https://get.docker.com | bash\r\nsystemctl start docker\r\nsystemctl enable docker\r\necho \"Docker 安装完成: $(docker --version)\"', '#!/bin/bash\r\necho \"开始卸载 Docker...\"\r\nsystemctl stop docker\r\nyum remove -y docker-ce docker-ce-cli containerd.io || apt-get remove -y docker-ce docker-ce-cli containerd.io\r\nrm -rf /var/lib/docker\r\necho \"Docker 卸载完成\"', NULL, 5, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
INSERT INTO `t_plugin_copy1` VALUES (6, 'Python 3', 'python3', 'BASE_ENV', 'Python 编程语言', 'LogoPython', '[\"3.10\", \"3.11\", \"3.12\"]', '3.11', '#!/bin/bash\r\necho \"开始安装 Python ${VERSION}...\"\r\nyum install -y python3 python3-pip || apt-get install -y python3 python3-pip\r\necho \"Python 安装完成: $(python3 --version)\"\r\necho \"Pip 版本: $(pip3 --version)\"', '#!/bin/bash\r\necho \"开始卸载 Python 3...\"\r\nyum remove -y python3 python3-pip || apt-get remove -y python3 python3-pip\r\necho \"Python 3 卸载完成\"', NULL, 6, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
