INSERT INTO `t_plugin_copy1` VALUES (8, 'Nginx', 'nginx', 'MIDDLEWARE', '高性能 Web 服务器', 'ServerSharp', '[\"1.24\", \"1.25\", \"latest\"]', 'latest', '#!/bin/bash\r\necho \"开始安装 Nginx...\"\r\nyum install -y nginx || apt-get install -y nginx\r\nsystemctl start nginx\r\nsystemctl enable nginx\r\necho \"Nginx 安装完成: $(nginx -v)\"', '#!/bin/bash\r\necho \"开始卸载 Nginx...\"\r\nsystemctl stop nginx\r\nyum remove -y nginx || apt-get remove -y nginx\r\necho \"Nginx 卸载完成\"', NULL, 1, 'ENABLED', '2025-12-01 11:52:33', '2025-12-01 11:52:33', 0);
