INSERT INTO `t_project` VALUES (1, 'mars-boot-test', '测试', 'https://gitee.com/Marsfactory/mars-springboot-deploy.git', 'dev', 'wqexpore@163.com', 'wq850994281', 1, '#!/bin/bash\n# 自动生成的部署脚本\n\nAPP_NAME=\"app\"\nAPP_PORT={{appPort}}\nDEPLOY_DIR=\"{{uploadPath}}\"\nJAR_FILE=$(ls $DEPLOY_DIR/*.jar | grep -v \'\\.bak$\' | head -n 1)\nLOG_FILE=\"$DEPLOY_DIR/app.log\"\n\necho \"========================================\"\necho \"开始部署应用\"\necho \"========================================\"\n\n# 1. 停止旧进程\necho \"[步骤1] 正在停止旧进程...\"\nPID=$(lsof -t -i:$APP_PORT 2>/dev/null)\nif [ ! -z \"$PID\" ]; then\n  kill -15 $PID\n  sleep 3\n  if ps -p $PID > /dev/null 2>&1; then\n    kill -9 $PID\n    echo \"已强制停止进程: $PID\"\n  else\n    echo \"已优雅停止进程: $PID\"\n  fi\nelse\n  echo \"未找到运行中的进程\"\nfi\n\nsleep 2\n\n# 2. 备份旧版本\necho \"[步骤2] 备份旧版本...\"\nif [ -f \"$DEPLOY_DIR/app.jar\" ]; then\n  BACKUP_NAME=\"app_$(date +%Y%m%d_%H%M%S).jar.bak\"\n  mv $DEPLOY_DIR/app.jar $DEPLOY_DIR/$BACKUP_NAME\n  echo \"已备份为: $BACKUP_NAME\"\n  ls -t $DEPLOY_DIR/*.jar.bak 2>/dev/null | tail -n +4 | xargs rm -f 2>/dev/null\nfi\n\n# 3. 重命名新上传的jar\necho \"[步骤3] 准备新版本...\"\nif [ -f \"$JAR_FILE\" ]; then\n  cp $JAR_FILE $DEPLOY_DIR/app.jar\n  echo \"已准备新版本: app.jar\"\nelse\n  echo \"错误: 未找到jar文件\"\n  exit 1\nfi\n\n# 4. 启动新应用\necho \"[步骤4] 正在启动应用...\"\ncd $DEPLOY_DIR\n\n# 清空旧日志\n> $LOG_FILE\n\n# 后台启动应用\nnohup java -jar -Xms512m -Xmx1024m -Dserver.port=$APP_PORT app.jar > $LOG_FILE 2>&1 &\nNEW_PID=$!\n\necho \"应用已启动，PID: $NEW_PID\"\necho \"日志文件: $LOG_FILE\"\n\n# 5. 等待应用启动并实时显示日志\necho \"[步骤5] 等待应用启动...\"\necho \"----------------------------------------\"\necho \"应用启动日志：\"\necho \"----------------------------------------\"\n\n# 实时显示日志并检测启动成功\nSTART_TIME=$(date +%s)\nTIMEOUT=60\nSTARTED=false\n\n# 使用 tail -f 实时显示日志，同时检测启动状态\n(\n  tail -f $LOG_FILE &\n  TAIL_PID=$!\n  \n  while true; do\n    CURRENT_TIME=$(date +%s)\n    ELAPSED=$((CURRENT_TIME - START_TIME))\n    \n    # 检查超时\n    if [ $ELAPSED -gt $TIMEOUT ]; then\n      kill $TAIL_PID 2>/dev/null\n      echo \"\"\n      echo \"----------------------------------------\"\n      echo \"警告: 应用启动超时（60秒）\"\n      echo \"请检查日志文件: $LOG_FILE\"\n      exit 1\n    fi\n    \n    # 检查端口是否已监听\n    if lsof -t -i:$APP_PORT > /dev/null 2>&1; then\n      sleep 2\n      kill $TAIL_PID 2>/dev/null\n      STARTED=true\n      break\n    fi\n    \n    # 检查进程是否还在运行\n    if ! ps -p $NEW_PID > /dev/null 2>&1; then\n      kill $TAIL_PID 2>/dev/null\n      echo \"\"\n      echo \"----------------------------------------\"\n      echo \"错误: 应用进程已退出\"\n      echo \"请检查日志文件: $LOG_FILE\"\n      exit 1\n    fi\n    \n    sleep 1\n  done\n  \n  if [ \"$STARTED\" = true ]; then\n    echo \"\"\n    echo \"----------------------------------------\"\n    echo \"应用启动成功！\"\n    echo \"========================================\"\n    echo \"部署完成！\"\n    echo \"应用端口: $APP_PORT\"\n    echo \"应用PID: $NEW_PID\"\n    echo \"日志文件: $LOG_FILE\"\n    echo \"========================================\"\n    exit 0\n  fi\n) || exit 1', 8888, 'JAVA', 'mvn clean package -DskipTests', 'target/*.jar', 1, '2025-11-28 11:37:12', '2025-11-28 15:54:19', 0);
